mapper.select:
select A.id, A.ban_ji_pai_ke_xin_xi_id, A.shang_ke_ri_qi, A.shang_ke_lao_shi_id, A.shang_ke_jiao_shi_id, A.shang_ke_shi_jian_start, A.shang_ke_shi_jian_end, A.shou_ke_ke_shi, A.shang_ke_nei_rong, A.pai_ke_ji_lu_zhuang_tai, A.dian_ming_shi_jian, B.pai_ke_gui_ze, C.xing_ming as shangKeLaoShiXingMing, D.ming_cheng as shangKeJiaoShiMingCheng, E.ke_cheng_id, E.id as banJiId, E.ming_cheng as banJiMingCheng, F.ming_cheng as keChengMingCheng from ban_ji_pai_ke_xin_xi B join pai_ke_ji_lu A on A.ban_ji_pai_ke_xin_xi_id = B.id left join lao_shi C on A.shang_ke_lao_shi_id = C.id left join shang_ke_jiao_shi D on A.shang_ke_jiao_shi_id = D.id join ban_ji E on B.ban_ji_id = E.id join ke_cheng F on E.ke_cheng_id = F.id
A.is_deleted = 0 and B.is_deleted = 0
and B.ban_ji_id = #{banJiId}
and A.shang_ke_lao_shi_id=#{shangKeLaoShiId}
and A.pai_ke_ji_lu_zhuang_tai in
#{paiKeJiLuZhuangTai}
and A.shang_ke_ri_qi > #{shangKeRiQiBegin}
and A.shang_ke_ri_qi < #{shangKeRiQiEnd}
order by A.update_time desc

select A.* from pai_ke_ji_lu A join pai_ke_ji_lu_shang_ke_xue_yuan_zu B on B.pai_ke_ji_lu_id = A.id
A.pai_ke_ji_lu_zhuang_tai = 'DAI_DIAN_MING' and A.ban_ji_pai_ke_xin_xi_id in
#{paiKeXinXinXiId}
and B.is_deleted = 0 and B.xue_yuan_id = #{shangKeXueYuanId}
