mapper.select:
select A.id, A.xue_yuan_id as xueYuanId, A.ke_cheng_id as keChengId, A.ke_cheng_zhuang_tai,A.ke_cheng_lei_xing, A.dan_jia, A.ke_cheng_shu_liang, A.zeng_song_ke_shi, A.you_hui_lei_xing, A.you_hui_shu_liang, A.sheng_yu_ke_shi, B.ming_cheng as keChengMingCheng, CC.*, D.xing_ming as xueYuanXingMing from xue_yuan_ke_cheng A join ke_cheng B on A.ke_cheng_id = B.id left join ( select A.id as banJiId, A.ming_cheng as banJiMingCheng, A.ke_cheng_id, C.xing_ming as banJiLaoShiXingMing from ban_ji A join ban_ji_ban_ji_xue_yuan_zu B on B.ban_ji_id = A.id left join lao_shi C on A.ban_ji_lao_shi_id = C.id
B.xue_yuan_id = #{xueYuanId} and B.is_deleted = 0
) CC on B.id = CC.ke_cheng_id join xue_yuan D on A.xue_yuan_id = D.id
A.is_deleted = 0 and A.xue_yuan_id = #{xueYuanId}
