mapper.select:
select A.id, A.bei_zhu,A.is_lao_shi, A.shou_ji,A.xing_bie, A.xing_ming,A.zhang_hao_lei_xing, A.mi_ma
from zhang_hao A

    A.shou_ji = #{shouJi}
    and A.zhang_hao_lei_xing = #{zhangHaoLeiXing}

select A.id, A.bei_zhu,A.is_lao_shi, A.shou_ji,A.xing_bie, A.xing_ming,A.zhang_hao_lei_xing, A.mi_ma
from zhang_hao A

    A.zhang_hao_lei_xing = #{zhangHaoLeiXing}
    and A.is_deleted = 0

order by A.update_time desc;
